query.sql:
  
	   select * from gc_work_record r
	   where r.project_id = ${@project_id}
	    and r.employee_id = ${@employee_id}
	    and r.work_date like '#{@date}%'

whenSql.when:
not exists (select 1 from gc_work_record 
		                                       where employee_id = ${@employee_id}
		                                        and project_id = ${@project_id}
		                                        and work_date = ${@work_date}
		                                       )

sqls.querySql:
       
		 select work_id from gc_work_record 
		  where employee_id = ${@employee_id}
		    and project_id = ${@project_id}
		    and work_date = ${@work_date}

collisionSql.where:
exists (select 1 from gc_work_record 
				  where work_id = ${@work_id}
				   and IFNULL(settlement_flag,'N') = 'Y')

exists (select 1 from gc_work_record 
				  where work_id = ${@work_id}
				   and IFNULL(settlement_flag,'N') = 'Y')
